static sqInt
allocateObjEntry(sqInt nSlots)
{
    sqInt dstIndex;
    sqInt i;
    sqInt iLimiT;
    sqInt srcIndex;


	/* First allocate nSlots in the GET */
	if (!(allocateGETEntry(nSlots))) {
		return 0;
	}
	if (!((getUsedGet()) == 0)) {

		/* Then move the GET upwards */
		srcIndex = getUsedGet();
		dstIndex = (getUsedGet()) + nSlots;
		for (i = 1, iLimiT = (getUsedGet()); i <= iLimiT; i += 1) {
			getBuffer[(dstIndex -= 1)] = (getBuffer[(srcIndex -= 1)]);
		}
	}
	getBuffer += nSlots;
	return 1;
}


