/*	Make sure that val1 is between val2 and val3. */

	/* BalloonEnginePlugin>>#assureValue:between:and: */
static sqInt
assureValuebetweenand(sqInt val1, sqInt val2, sqInt val3)
{
	if (val2 > val3) {
		if (val1 > val2) {
			return val2;
		}
		if (val1 < val3) {
			return val3;
		}
	}
	else {
		if (val1 < val2) {
			return val2;
		}
		if (val1 > val3) {
			return val3;
		}
	}
	return val1;
}

