{
    sqInt x0;
    sqInt x1;

	x0 = ((usqInt) (spanStartGet())) >> (aaShiftGet());
	x1 = (((usqInt) (spanEndGet())) >> (aaShiftGet())) + 1;
	if (x0 < 0) {
		x0 = 0;
	}
	if (x1 > (spanSizeGet())) {
		x1 = spanSizeGet();
	}
	while (x0 < x1) {
		spanBuffer[x0] = 0;
		x0 += 1;
	}
	spanStartPut(spanSizeGet());
	spanEndPut(0);
	return 0;
}

