	/* BalloonEngineBase>>#storeEdgeStateFrom:into: */
static sqInt
storeEdgeStateFrominto(sqInt edge, sqInt edgeOop)
{
	if ((slotSizeOf(edgeOop)) < ETBalloonEdgeDataSize) {
		return primitiveFail();
	}
	storeIntegerofObjectwithValue(ETIndexIndex, edgeOop, objectIndexOf(edge));
	storeIntegerofObjectwithValue(ETXValueIndex, edgeOop, edgeXValueOf(edge));
	storeIntegerofObjectwithValue(ETYValueIndex, edgeOop, currentYGet());
	storeIntegerofObjectwithValue(ETZValueIndex, edgeOop, edgeZValueOf(edge));
	storeIntegerofObjectwithValue(ETLinesIndex, edgeOop, edgeNumLinesOf(edge));
	/* begin lastExportedEdgePut: */
	workBuffer[GWLastExportedEdge] = edge;
	return 0;
}

